mapper.select:
/* cateDao.selectCateUnList - 하위 카테고리 리스트 */ SELECT GOODS_CATA_SEQ AS goodsCataSeq , CATGRY_CD AS catgryCd , GOODS_CD AS goodsCd , CATGRY_NM AS catgryNm , NOTE_CONT AS noteCont , CLASS_LVL_CD AS classLvlCd , UPR_CLASS_CD AS uprClassCd , RGST_ID AS rgstId , RGST_DATE AS rgstDate , UPDT_ID AS updtId , UPDT_DATE AS updtDate , USE_YN AS useYn FROM CLT_GOODS_CATA WHERE 1=1
AND UPR_CLASS_CD = #{uprClassCd}
AND CLASS_LVL_CD = '2' ORDER BY GOODS_CATA_SEQ ASC

mapper.update:
/* cateDao.updateCate - 카테고리 수정 */
UPDATE CLT_GOODS_CATA 
SET CATGRY_CD  = #{catgryCd}
	, CATGRY_NM  = #{catgryNm}
	, NOTE_CONT  = #{noteCont}
	, USE_YN  = #{useYn}
	, UPDT_ID  = #{updtId}
	, UPDT_DATE  = NOW()
WHERE 1=1
AND GOODS_CATA_SEQ = #{goodsCataSeq}

mapper.delete:
/* cateDao.deleteCate - 카테고리 삭제 */
DELETE FROM CLT_GOODS_CATA 
WHERE 1=1 
AND GOODS_CATA_SEQ = #{goodsCataSeq}
