mapper.insert:
/* dressDao.insertDress - 드레스룸 등록 */
INSERT INTO CLT_SALE_BOARD_RVW
( SALE_BOARD_SEQ
, GOODS_CD
, ENTR_NO
, BUL_TIT_NM
, BUL_CONT
, RPY_LVL

, PAR_SALE_BOARD_RPY_SN


, QRY_CNT


, LKE_CNT

, RGST_ID
, RGST_DATE
, UPDT_ID
, UPDT_DATE
, USE_YN
, IMG_PATH
, IMG_NM
, ORD_NO
, RVW_TAG
, ORD_DTL_NO
) VALUES
( #{saleBoardSeq}
, #{goodsCd}
, #{entrNo}
, #{bulTitNm}
, #{bulCont}
, #{rpyLvl}

, #{parSaleBoardRpySn}


, #{qryCnt}


, #{lkeCnt}

, #{rgstId}
, NOW()
, #{updtId}
, NOW()
, 'Y'
, #{imgPath}
, #{imgNm}
, #{ordNo}
, #{rvwTag}
, #{ordDtlNo}
)

mapper.update:
/* dressDao.updateDress - 드레스룸 수정 */
UPDATE CLT_SALE_BOARD_RVW 
SET BUL_TIT_NM = #{bulTitNm}
	, BUL_CONT = #{bulCont}
	, USE_YN = #{useYn}

	, IMG_PATH = #{imgPath}


	, IMG_NM = #{imgNm}

	, RVW_TAG = #{rvwTag}
WHERE 1=1
AND SALE_BOARD_RPY_SEQ = #{saleBoardRpySeq}

mapper.delete:
/* dressDao.deleteDress - 드레스룸 삭제 */
DELETE FROM CLT_SALE_BOARD_RVW
WHERE 1=1
AND SALE_BOARD_RPY_SEQ = #{saleBoardRpySeq}

mapper.select:
/* dressDao.selectWriteSaleRvw - 드레스룸 */ SELECT ORD.ORD_NO AS ordNo , ORD.USR_ID AS usrId , ORD.ENTR_NO AS entrNo , ORD.ORD_STAT AS ordStat , ORD.ORDR_ID AS ordrId , ORD.ORDR_NM AS ordrNm , ORD.ORDR_PHON AS ordrPhon , ORD.ORDR_EMAIL AS ordrEmail , ORD.ACQR_PHON AS acqrPhon , ORD.ACQR_NM AS acqrNm , ORD.ACQR_ADDR AS acqrAddr , ORD.ACQR_ADDR_DTL AS acqrAddrDtl , ORD.ACQR_EMAIL AS acqrEmail , ORD.PRCL_WAY AS prclWay , ORD.PACK_WAY AS packWay , ORD.PAY_MNY AS payMny , ORD.REQ_MEMO AS reqMemo , ORD.ORD_DATE AS ordDate , ORD.UPDT_DATE AS updtDate , ORD.UPDT_ID AS updtId , ORD.RGST_DATE AS rgstDate , ORD.RGST_ID AS rgstId , ORD.BILL_NUM AS billNum , ORD.SALE_BOARD_SEQ AS saleBoardSeq , SBRW.SALE_BOARD_RPY_SEQ AS saleBoardRpySeq , SBRW.RPY_LVL AS rpyLvl , SBRW.PAR_SALE_BOARD_RPY_SN AS parSaleBoardRpySn , SBRW.QRY_CNT AS qryCnt , SBRW.LKE_CNT AS lkeCnt , SBRW.USE_YN AS useYn , GOODS.IMG_PATH AS imgPath , GOODS.IMG_NM AS imgNm , SALE.BUL_TIT_NM AS bulTitNm , SALE.BUL_CONT AS bulCont , DTL.ORD_DTL_NO AS ordDtlNo, DTL.GOODS_CD AS goodsCd FROM CLT_ORD_BASE ORD LEFT OUTER JOIN CLT_SALE_BOARD_RVW SBRW ON ORD.ORD_NO = SBRW.ORD_NO LEFT OUTER JOIN CLT_ORD_DTL DTL ON DTL.ORD_NO = ORD.ORD_NO LEFT OUTER JOIN CLT_GOODS_INFO GOODS ON DTL.GOODS_CD = GOODS.GOODS_CD LEFT OUTER JOIN CLT_SALE_BOARD SALE ON DTL.GOODS_CD = SALE.GOODS_CD WHERE 1=1
AND ORD.USR_ID = #{usrId}
AND DTL.ORD_DTL_NO = #{ordDtlNo}
