mapper.select:
/* goodsDao.selectGoodsPagingList - 상품 페이징 리스트 */ SELECT GOODS_INFO_SEQ AS goodsInfoSeq /*상품 시퀀스*/ , GOODS_CD AS goodsCd /*상품 코드*/ , ENTR_NO AS entrNo /*거래처 번호*/ , GOODS_NM AS goodsNm /*상품이름*/ , GOODS_PRC AS goodsPrc /*상품가격*/ , CATGRY_CD AS catgryCd /*대분류*/ , CATGRY_CD2 AS catgryCd2 /*소분류*/ , INY_QTY AS inyQty /*재고수량*/ , DLV_PRC AS dlvPrc /*배송비*/ , GOODS_SIZE AS goodsSize /*상품싸이즈*/ , GOODS_CLR AS goodsClr /*상품컬러*/ , IMG_PATH AS imgPath /*이미지경로*/ , IMG_NM AS imgNm /*이미지이름*/ , GOODS_INTR AS goodsIntr /*상품소개*/ , SALE_STAT_CD AS saleStatCd /*판매상태*/ , USE_YN AS useYn /*사용여부*/ , RGST_ID AS rgstId /*등록자*/ , RGST_DATE AS rgstDate /*등록일*/ , UPDT_ID AS updtId /*수정자*/ , UPDT_DATE AS updtDate /*수정일*/ FROM CLT_GOODS_INFO WHERE 1=1
AND ENTR_NO = #{entrNo }
LIMIT #{startIdx }, #{pagePerRows }
