mapper.update:
UPDATE CLT_ORD_BASE 
	SET USR_ID  = #{usrId}
	, ENTR_NO  = #{entrNo}
	, ORD_STAT  = #{ordStat}
	, ORDR_ID  = #{ordrId}
	, ORDR_NM  = #{ordrNm}
	, ORDR_PHON  = #{ordrPhon}
	, ORDR_EMAIL  = #{ordrEmail}
	, ACQR_PHON  = #{acqrPhon}
	, ACQR_NM  = #{acqrNm}
	, ACQR_ADDR  = #{acqrAddr}
	, ACQR_ADDR_DTL  = #{acqrAddrDtl}
	, ACQR_EMAIL  = #{acqrEmail}
	, PRCL_WAY  = #{prclWay}
	, PACK_WAY  = #{packWay}
	, PAY_WAY  = #{payWay}
	, PAY_MNY  = #{payMny}
	, REQ_MEMO  = #{reqMemo}
	, ORD_DATE  = NOW()
	, UPDT_DATE  = NOW()
	, UPDT_ID  = #{updtId}
	, BILL_NUM = #{billNum}
WHERE 1=1
AND ORD_NO = #{ordNo}

mapper.delete:
DELETE FROM CLT_ORD_BASE
WHERE 1=1 
AND ORD_NO = #{ordNo}

