mapper.update:
/* cltOrderMapper - admOrdBaseUpdate */
	UPDATE CLT_ORD_BASE 
		SET CLM_SCT_CD = #{clmSctCd}
		, CLM_STAT_CD = #{clmStatCd}
	WHERE 1=1
	AND ORD_NO = #{ordNo}
	AND ORD_CLM_NO = #{ordClmNo}

/* cltOrderMapper - admOrdDtlUpdate */
	UPDATE CLT_ORD_DTL 
		SET ORD_DTL_STAT_CD = #{ordDtlStatCd}

		, UPDT_DATE  = NOW()
		, UPDT_ID  = #{updtId}
	WHERE 1=1
	AND ORD_CLM_NO = #{ordClmNo}
	AND ORD_CLM_DTL_SN = #{ordClmDtlSn}

